sys_script_include.script:
var CoursesAjax = Class.create();
CoursesAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    getCourses: function() {
        var courses = [];
        var courseGR = new GlideRecord('u_course');
        courseGR.query();
        while (courseGR.next()) {
            courses.push({
                sys_id: courseGR.getUniqueValue(),
                title: courseGR.getValue('title'),
                description: courseGR.getValue('description')
            });
        }
        return JSON.stringify(courses);
    },
    subscribeToCourse: function() {
        var courseID = this.getParameter('sysparm_course_id');
        var userSysID = gs.getUserID(); // Getting the ID of the current user

        var subscription = new GlideRecord('u_course_subscription');
        subscription.initialize();
        subscription.learner = this.getParameter('sysparm_learner_id'); // Assuming you have a way to get the learner ID
        subscription.course = courseID;
        subscription.subscription_date = gs.nowDateTime();
        return subscription.insert() ? 'success' : 'failure';
    }
});
